
public class ComputeVolume {

	public static void main(String[] args) {
		double radius = 20;
		double area = radius*radius*3.14159;;
		double height = 30;
		double volume;
		volume = area*height;
		// TODO Auto-generated method stub
		System.out.println("The volume for the cylinder of radius "+radius+" is " +volume);		

	}

}
